import React, { useEffect, useState } from 'react';
import Header from './Header';
import HomeMainSection from './HomeMainSection';
import Footer from './Footer';
import reviewsData from '../data/reviews'; 

const Homepage = () => {
  const [randomReviews, setRandomReviews] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetching reviews
    const randomIndices = [];
    while (randomIndices.length < 2) {
      const randomIndex = Math.floor(Math.random() * reviewsData.length);
      if (!randomIndices.includes(randomIndex)) {
        randomIndices.push(randomIndex);
      }
    }
    const selectedReviews = randomIndices.map(index => reviewsData[index]);
    setRandomReviews(selectedReviews);

    // Fetching products
    const fetchProducts = async () => {
      try {
        // Replace with your actual API endpoint
        const response = await fetch('/api/products'); 
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          throw new Error('Products could not be fetched');
        }
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <Header />
      <HomeMainSection reviews={randomReviews} />
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
          </div>
        ))}
      </div>
      <Footer />
    </div>
  );
};

export default Homepage;
