import React, { useState } from 'react';

const SignupForm = ({ onSwitch }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [generalError, setGeneralError] = useState('');
  const [signupMessage, setSignupMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    setGeneralError('');
    setSignupMessage('');

    if (!username || !password || !confirmPassword || !email) {
      setGeneralError('All fields are required!');
      return;
    }

    if (password !== confirmPassword) {
      setGeneralError('Passwords do not match!');
      return;
    }

    fetch('/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password, email }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.error) {
        setSignupMessage(data.error);
      } else {
        setSignupMessage('Signup successful! You can now login.');
        // Optionally clear the form or redirect the user to the login page
      }
    })
    .catch(error => {
      console.error('Error during signup:', error);
      setSignupMessage('An error occurred during signup. Please try again.');
    });
  };

  return (
    <div>
      <h2>Signup</h2>
      {generalError && (
        <div style={{ color: 'red'}}>
          <div className="error">{generalError}</div>
          <div style={{ height: '1em' }}></div> 
        </div>
      )}
      <form onSubmit={handleSubmit}>

        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter password"
          />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm password"
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter email"
          />
        </div>
        <div>
        <button type="submit">Signup</button>
      </div>
      <div> 
        <button type="button" onClick={onSwitch}>Switch to Login</button>
      </div>
      </form>
    </div>
  );
};

export default SignupForm;
